#function to get the eigenvalues of the S matrix given a complex wL
def newfunc(Re_wL, Im_wL):
  wL= Re_wL+complex(0,1)*Im_wL
  w=wL
  n = np.array([np.complex(2, 0.208), np.complex(2, -0.208)])
  d = 0.5*n*w

  arr = []
  for i in range(2):
    arr.append(np.array( [ [ np.cos(d[i]) , np.sin(d[i])/(w*n[i]) ] , [ (-1)*np.sin(d[i])*w*n[i] , np.cos(d[i]) ] ] ))
  alpha = np.stack(arr, 0)

  ML = arr[0]
  ML = np.matmul(ML,alpha[1])

  MR = arr[1]
  MR = np.matmul(MR,alpha[0])

  rL = ((ML[1][0]) + (w*w*ML[0][1]) + (np.complex(0,1)*w*(ML[1][1] - ML[0][0])))/((-1*ML[1][0]) + (w*w*ML[0][1]) + (np.complex(0,1)*w*(ML[1][1] + ML[0][0])))
  t = (2*np.complex(0,1)*w*np.exp(np.complex(0,1)*(-1)*wL))/((-1*MR[1][0]) + (w*w*MR[0][1]) + (np.complex(0,1)*w*(MR[1][1] + MR[0][0])))
  rR = ((MR[1][0]) + (w*w*MR[0][1]) + (np.complex(0,1)*w*(MR[1][1] - MR[0][0])))/((-1*MR[1][0]) + (w*w*MR[0][1]) + (np.complex(0,1)*w*(MR[1][1] + MR[0][0])))

  s = np.array([[t, rR],
                    [rL, t]])

  eig,_ = np.linalg.eig(s)
  return (eig[0],eig[1])

